import React from "react";
import { useFormik } from 'formik';
import { useNavigate } from "react-router-dom";
import axios from 'axios'

const validate=values =>{
    const errors={};
     
     if(!values.firstName)
       {
           errors.firstName="First Name is required"
       }
    else if(values.firstName.length>20)
       {
          errors.firstName='Must be 20 characters or less'
       }

       if(!values.lastName)
        {
            errors.lastName='Last Name is Required'
        }
        else if(values.lastName.length>25)
         {
             errors.lastName='Must be 25 characters or less'
         }


         if(!values.email){
             errors.email="Email is Required"
         }
         else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email))
         {
             errors.email='Invalid email address'
         }

         if(!values.date_of_join){
            errors.course="Date of Join is required"
        }

        if(!values.salary){
            errors.course="Salary is required"
        }

         return errors;
}


export default function CreateTeacher(){

    const navigate=useNavigate()
    const formik=useFormik({
         
          initialValues:{
              firstName:'',
              lastName:'',
              email:'',
              date_of_join:'',
              salary:0

          },
          validate,
          onSubmit:values=>{
              console.log(formik.values)
              alert(JSON.stringify(formik.values))
              postTeacher(formik.values)

          }
    })

    const postTeacher=(values)=>{
        axios.post('https://629ef6b78b939d3dc28b227c.mockapi.io/teachers',formik.values)
        .then((resp)=>{
            console.log(resp)
            navigate('/teacher')
           
        })
        .catch((err)=>{
            console.log(err)
        })
    }
    return(
        <div>
             <form onSubmit={formik.handleSubmit}>
                <label id='create-firstName'>First Name</label>
                <input type='text' id='firstName' name="firstName"  value={formik.values.firstName}  onBlur={formik.handleBlur} onChange={formik.handleChange}></input>
                {formik.touched.firstName && formik.errors.firstName ?<div>{formik.errors.firstName}</div>:null}
                
                <label id='create-lastName'>Last Name</label>
                <input type='text' id='lastName' name='lastName'  value={formik.values.lastName}  onBlur={formik.handleBlur} onChange={formik.handleChange}></input>
                 {formik.touched.lastName && formik.errors.lastName ? <div>{formik.errors.lastName}</div> : null}
                 
                 <label id='create-email'>Email</label>
                 <input type='text' id='email' name='email'  value={formik.values.email}  onBlur={formik.handleBlur} onChange={formik.handleChange}></input>
                 {formik.touched.email && formik.errors.email ? <div>{formik.errors.email}</div> : null}

                 <label id='date_of_join' >Date of Join</label>
                <input type='date' id='date_of_join' name='date_of_join'  value={formik.values.date_of_join}  onBlur={formik.handleBlur} onChange={formik.handleChange}></input>
                {formik.touched.date_of_join && formik.errors.date_of_join ? <div>{formik.errors.date_of_join}</div> : null}

                <label id='salary' >salary</label>
                <input type='number' id='salary' name='salary' value={formik.values.salary} onBlur={formik.handleBlur} onChange={formik.handleChange}></input>
                   {formik.touched.salary && formik.errors.salary ? <div>{formik.errors.salary}</div> : null}
              
                 <button type='submit'  className="bg-danger">Create</button>
            </form>

        </div>
    )
}
