import axios from "axios";
import React, { useEffect, useState } from "react";
import { Formik } from 'formik';
import Select from 'react-select';

export default function AssignMentor(){

    const [students,setStudent]=useState('')

    const [teachers,setTeachers]=useState('')

    const getStudents=()=>{
        axios.get('http://localhost:8000/createStudent')
        .then((res)=>{
            console.log(res)
            setStudent(res.data)
          
        })
        .catch((err)=>{
            console.log(err)

        })
    }


    const getTeachers=()=>{
        axios.get('http://localhost:8000/createTeacher')
        .then((res)=>{
            console.log(res)
            setTeachers(res.data)
        })
        .catch((err)=>{
            console.log(err)
        })
    }
    useEffect(()=>{
         getStudents()
         getTeachers()
    },[])

    return(

      
<Formik
            initialValues={{
                flavors: ""
            }}
            onSubmit={values => alert(JSON.stringify(values, null, 2))}
            >
            {
                props => {
                    const {
                        values,
                        handleSubmit,
                        handleBlur,
                        setFieldValue
                    } = props;
                    return (
                        <form onSubmit={handleSubmit}>
                            <div style={{width: "200px", marginBottom: "15px"}}>
                                <Select
                                    id={"flavors"}
                                    type={"text"}
                                    value={values.flavors}
                                    onChange={option => setFieldValue("flavors", option)}
                                    options={options}
                                    onBlur={handleBlur}
                                />
                            </div>

                            <div style={{width: "200px", marginBottom: "15px"}}>
                                <Select
                                    id={"flavors"}
                                    type={"text"}
                                    value={values.flavors}
                                    onChange={option => setFieldValue("flavors", option)}
                                    options={options}
                                    onBlur={handleBlur}
                                />
                            </div>
                            <button>Submit</button>
                        </form>
                    );
                }
            }
        </Formik>
    )
}
