import React from "react";
import { useFormik } from 'formik';
import axios from 'axios'

const validate=values =>{
    const errors={};
     
     if(!values.firstName)
       {
           errors.firstName="First Name is required"
       }
    else if(values.firstName.length>20)
       {
          errors.firstName='Must be 20 characters or less'
       }

       if(!values.lastName)
        {
            errors.lastName='Last Name is Required'
        }
        else if(values.lastName.length>25)
         {
             errors.lastName='Must be 25 characters or less'
         }


         if(!values.email){
             errors.email="Email is Required"
         }
         else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email))
         {
             errors.email='Invalid email address'
         }

         if(!values.course){
            errors.course="Must select the course"
        }

         return errors;
}

export default function CreateStudent(){

    const options=[
        {
            label:"...Choose a course...",
            value:"choose"
        },
       
        {
            label:"Information Technology",
            value:"information technology"
        },
        {
            label:"Computer Science Engineering",
            value:"computer science engineering"
        },
        {
            label:"Computer Science and System Engineering",
            value:"computer science and system engineering"
        },
        {
            label:"Mechanical Engineering",
            value:"mechanical engineering"
        },
        {
            label:"Civil Engineering",
            value:"civil engineering"
        },

    ]

    const formik=useFormik({
         
          initialValues:{
              firstName:'',
              lastName:'',
              email:'',
              course:''
          },
          validate,
          onSubmit:values=>{
              console.log(formik.values)
              alert(JSON.stringify(formik.values))
              postStudents(formik.vaalues)

          }
    })

    const postStudents=(values)=>{
        axios.post('https://629ef6b78b939d3dc28b227c.mockapi.io/students',formik.values)
        .then((resp)=>{
            console.log(resp)
           
        })
        .catch((err)=>{
            console.log(err)
        })
    }

    return(
        
        <div>
            <form onSubmit={formik.handleSubmit}>
                <label id='create-firstName'>First Name</label>
                <input type='text' id='firstName' name="firstName"  value={formik.values.firstName}  onBlur={formik.handleBlur} onChange={formik.handleChange}></input>
                {formik.touched.firstName && formik.errors.firstName ?<div>{formik.errors.firstName}</div>:null}
                
                <label id='create-lastName'>Last Name</label>
                <input type='text' id='lastName' name='lastName'  value={formik.values.lastName}  onBlur={formik.handleBlur} onChange={formik.handleChange}></input>
                 {formik.touched.lastName && formik.errors.lastName ? <div>{formik.errors.lastName}</div> : null}
                 
                 <label id='create-email'>Email</label>
                 <input type='text' id='email' name='email'  value={formik.values.email}  onBlur={formik.handleBlur} onChange={formik.handleChange}></input>
                 {formik.touched.email && formik.errors.email ? <div>{formik.errors.email}</div> : null}

                <label id='courses'>Courses</label>
               
                 <select name='course' id='course' value={formik.values.option} onChange={formik.handleChange}>
                  
                   {options.map((option) => (
                     <option value={option.value}>{option.label}</option>
                   ))}
                    
                </select>
                {formik.touched.course && formik.errors.course ? <div>{formik.errors.course}</div> : null}

              
                
                 <button type='submit'  className="bg-danger">Create</button>
            </form>

        </div>
    )


}
