import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams ,useNavigate} from "react-router-dom";


export default function Edit(){

     const {id} =useParams()
     const [students,setStudent]=useState('')
     const [errors ,setErrors]=useState('')
     const [count,setCount]=useState(0)
     const navigate=useNavigate()

     const getStudentDetails=()=>{
         axios.get(`https://629ef6b78b939d3dc28b227c.mockapi.io/students/${id}`)
         .then((res)=>{
             setStudent(()=>setStudent(res.data))
         })
         .catch((err)=>{
             console.log(err)
         })
     }

     const handleChange=(e)=>{

          let name=e.target.name
          let value=e.target.value

          setStudent((prevState)=>({
              ...prevState,
             [name]:value

          }))
    }

    const handleSubmit=(event)=>
    {
        event.preventDefault()
        console.log(students)
        setCount(count+1)
        setErrors(validate(students))
       
        console.log(count)
       
    }

     const updateStudentDetails=()=>{
        if(Object.keys(errors).length===0)
        {
             axios.put(`https://629ef6b78b939d3dc28b227c.mockapi.io/students/${id}`,students)
             
             .then((res)=>{
                 console.log(res)
                 setTimeout(()=>{
                   navigate('/student')
                 },[2000])

             })
             .catch((err)=>{
                 console.log(err)
             })
            }
     
            }  

            const validate=values =>{
                const errors={};
                 
                 if(!values.firstName)
                   {
                       errors.firstName="First Name is required"
                   }
                else if(values.firstName.length>20)
                   {
                      errors.firstName='Must be 20 characters or less'
                   }
            
                   if(!values.lastName)
                    {
                        errors.lastName='Last Name is Required'
                    }
                    else if(values.lastName.length>25)
                     {
                         errors.lastName='Must be 25 characters or less'
                     }
            
            
                     if(!values.email){
                         errors.email="Email is Required"
                     }
                     else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email))
                     {
                         errors.email='Invalid email address'
                     }
            
                     if(!values.course){
                        errors.course="Must select the course"
                    }
            
                     return errors;
            }

     useEffect(()=>{
       
        getStudentDetails()   
        
     },[])
     useEffect(()=>{
         if(count>0)
         {
        updateStudentDetails()
        console.log(count)  
         }      
     },[(count)])

   

    return(

        <div>       
            {
            (students) ?
            
            <form onSubmit={handleSubmit} >
                <p className="text-danger fs-2"> Update Student</p>
                <input type='text' id='firstName'  name='firstName'  placeholder="First Name" value={students.firstName} onChange={handleChange}/>
                <p>{errors.firstName}</p>
                <input type='text' id='lastName'  name='lastName' placeholder="Last Name" value={students.lastName} onChange={handleChange}/>
                <p>{errors.lastName}</p>
                <input type='email' id='Email' name='email' placeholder="Email" value={students.email} onChange={handleChange}/>
                <p>{errors.email}</p>
                 <label id='courses'>Course</label>
                <select  name='course' id='course' defaultValue={"default"} value={students.course} onChange={handleChange}>
                    <option value={"default"} >Choose an option</option>
                   <option value='information technology'>Information Technology</option>
                   <option value='computer science engineering'>Computer Science Engineering</option>
                   <option value='computer science and system engineering'>Computer Science And System Engineering</option>
                   <option value='civil engineering'>Civil Engineering</option>
                   <option value='mechanical engineering'>Mechanical Engineering</option>
                </select>
                <p>{errors.course}</p>

                <button type='submit' id='update-student'  className="bg-danger">Update</button>
            </form>
           
           :' '
           }
        </div>

    )

        }
